    <ul>
      <li parentName="ul">{`[Part 1]`}{`({{ site.baseurl }}{% link _posts/2019-09-08-kuar-part1.md %})`}</li>
      <li parentName="ul">{`[Part 2]`}{`({{ site.baseurl }}{% link _posts/2019-09-09-kuar-part2.md %})`}</li>
      <li parentName="ul">{`[Part 3]`}{`({{ site.baseurl }}{% link _posts/2019-09-12-kuar-part3.md %})`}</li>
      <li parentName="ul">{`[Part 4]`}{`({{ site.baseurl }}{% link _posts/2019-09-15-kuar-part4.md %})`}</li>
    </ul>

    </MDXLayout>;
}

